mapper.update:
/* Board - boardUpdate */
UPDATE	 BOARD
SET		 BTITLE = #{btitle, jdbcType=VARCHAR}
		,BCONTENT = #{bcontent, jdbcType=VARCHAR}
		,BUPDATEDATE = SYSDATE

		,BFILE = #{bfile, jdbcType=VARCHAR}

WHERE	 BNO = #{bno, jdbcType=VARCHAR}
AND		 BDELETEYN = 'Y'

/* Board - boardDelete */
UPDATE	 BOARD
SET		 BDELETEYN = 'N'
		,BUPDATEDATE = SYSDATE
WHERE	 BNO = #{bno, jdbcType=VARCHAR}
AND		 BDELETEYN = 'Y'
