mapper.select:
SELECT 
	COUNT(*)
FROM
	FAVORITES
WHERE
	MNO = #{mno, jdbcType=VARCHAR}

AND
	rcp_seq = #{rcp_seq, jdbcType=VARCHAR}


AND
	RBNO = #{rbno, jdbcType=VARCHAR}

mapper.insert:
INSERT INTO FAVORITES(
	 FNO 
	,MNO
	,RBNO
	,RCP_SEQ
	,FINSERTDATE
	)
VALUES(
	 #{fno, jdbcType=VARCHAR}
	,#{mno, jdbcType=VARCHAR}
	,#{rbno, jdbcType=VARCHAR}
	,#{rcp_seq, jdbcType=VARCHAR}
	,SYSDATE
	)

mapper.delete:
DELETE FAVORITES
WHERE
	MNO = #{mno, jdbcType=VARCHAR}

AND
	rcp_seq = #{rcp_seq, jdbcType=VARCHAR}


AND
	RBNO = #{rbno, jdbcType=VARCHAR}
