mapper.insert:
INSERT INTO REPLY(
				  RNO
				 ,MNO
				 ,BNO
				 ,RBNO
				 ,RWRITER
				 ,RCONTENT
				 ,RINSERTDATE
				 ,RUPDATEDATE
				 ,RDELETEYN
				 )
VALUES			 (
				  #{rno, jdbcType=VARCHAR}
				 ,#{mno, jdbcType=VARCHAR}
				 ,#{bno, jdbcType=VARCHAR}
				 ,#{rbno, jdbcType=VARCHAR}
				 ,#{rwriter, jdbcType=VARCHAR}
				 ,#{rcontent, jdbcType=VARCHAR}
				 ,SYSDATE
				 ,SYSDATE
				 ,'Y' 
				 )

mapper.update:
UPDATE   REPLY
SET		 RCONTENT = #{rcontent, jdbcType=VARCHAR}
		,RUPDATEDATE = SYSDATE
WHERE	 RNO = #{rno, jdbcType=VARCHAR}
AND		 RDELETEYN = 'Y'
